const feeds = ($json.feeds || []).map(s => (s || '').trim());
const keywords = $json.keywords || [];

return feeds.map(f => ({ json: { feed: f, keywords } }));

const results = [];

for (const item of $input.all()) {
  const channel = item.json?.rss?.channel;;

  if (!channel) continue;


  const items = Array.isArray(channel.item) ? channel.item : [channel.item];

  for (const entry of items) {

    const getText = (field) => {
      if (!field) return "";
      if (Array.isArray(field)) {
        const val = field[0];
        if (typeof val === "string") return val;
        if (typeof val === "object") return val._ || "";
      }
      if (typeof field === "object") return field._ || "";
      return String(field);
    };

    const title = getText(entry.title);
    const link = getText(entry.link);
    const desc = getText(entry.description) || getText(entry["content:encoded"]);
    const date = getText(entry.pubDate);
    const source = (link.split("/")[2] || "unknown").toLowerCase();

    results.push({ json: { title, link, description: desc, pubDate: date, source } });
  }
}

return results;

// Collect all parsed articles
const articles = $input.all().map(i => i.json);

// Your interest keywords
const keywords = [
  "AI", "startup", "finance", "TRU", "Canada",
  "education", "biology", "space", "NASA", "software", "web development"
].map(k => k.toLowerCase());

// Score each article by keyword match
const scored = articles.map(a => {
  const text = (a.title + " " + a.description).toLowerCase();
  const score = keywords.reduce((sum, k) => sum + (text.includes(k) ? 1 : 0), 0);
  return { ...a, score };
});

// Keep only relevant ones (score â‰¥ 1)
const filtered = scored.filter(a => a.score > 0);

// Sort by score (best first)
filtered.sort((a, b) => b.score - a.score);

// Limit to top 10
return filtered.slice(0, 10).map(a => ({ json: a }));

