const originals = $items('Code in JavaScript1', 0); // 0=input index

const out = llmItems.map((itm, idx) => {
  const base = originals[idx]?.json ?? {};
  const summary = itm.json.summary || itm.json.text || itm.json.response || '';
  return { json: { ...base, summary } };
});

return out;

